import osfrom flask import Flask, jsonify, requestapp = Flask(__name__)@app.route('/', methods = ["GET","POST"])def index():	print(request)	return request	@app.route('/login', methods = ["POST"])def login():	print(request.data)	data = json.loads(request.data)	if data["user"] == "admin" and data["pass"] == "admin":		auth = {			"auth": True,			"token": "token",			"message": "Logged in succesfully."		}		return json.dumps(auth)	auth = {		"auth": False,		"token": None,		"message": "Invalid credentials."	}	return json.dumps(auth)def verifyToken(token):	return token == "token"	@app.route('/teste', methos = ["GET"])def teste():		if not verifyToken(request.headers.get('Authorization')):			return 'error'		return 'deu'		if __name__ == "__main__":	port = int(os.environ.get("PORT", 5000))	app.run(host='0.0.0.0', port=port)